public class MissingNumber {
    public static void main(String[] args) {
        int[] arr = {1, 2, 4, 5, 6};
        System.out.println("Missing number: " + findMissing(arr));
    }

    public static int findMissing(int[] arr) {
        int n = arr.length + 1;
        int total = n * (n + 1) / 2;
        for (int num : arr) {
            total -= num;
        }
        return total;
    }
}
